import React, { useState, createContext, useContext } from 'react';

interface TabProps {
    id: string;
    title: string;
    children: React.ReactNode;
}

const TabContext = createContext({ activeTab: '', setActiveTab: (id: string) => {} });

const Tab:React.FC<TabProps> = ({ id, title, children }) => {
    const { activeTab } = useContext(TabContext);
    return activeTab === id ? <>{children}</> : null;
};

interface TabsControlProps {
    children: React.ReactNode;
}

const TabsControl: React.FC<TabsControlProps> = ({ children }) => {
    const { activeTab, setActiveTab } = useContext(TabContext);
    return (
        <div>
            {React.Children.map(children, (child) =>
                React.cloneElement(child as React.ReactElement, {
                    activeTab,
                    setActiveTab,
                })
            )}
        </div>
    );
};

interface TabsProps {
    children: React.ReactNode;
}

const Tabs: React.FC<TabsProps> = ({ children }) => {
    const [activeTab, setActiveTab] = useState('1');
    return (
        <TabContext.Provider value={{ activeTab, setActiveTab }}>
            {children}
        </TabContext.Provider>
    );
};

export { Tabs, TabsControl, Tab };
