import './Auth.scss';
import { useState } from 'react';
import { FormLogin, FormRegistration, FormRestore } from './components/Forms';
import { Tabs, TabsControl, Tab } from '../tabs/Tabs';

function AuthForm() {
    const [activeTab, setActiveTab] = useState('1');

    return (
        <div className="auth col-3 p-0">
            <div className="auth-switchers py-3">
                <TabsControl>
                    <button onClick={() => setActiveTab('1')}>Tab 1</button>
                    <button onClick={() => setActiveTab('2')}>Tab 2</button>
                </TabsControl>
                <Tabs>
                    <Tab id="1" title="Tab 1">
                        <div>Content 1</div>
                    </Tab>
                    <Tab id="2" title="Tab 2">
                        <div>Content 2</div>
                    </Tab>
                </Tabs>
            </div>
        </div>
    );
}

export default AuthForm;
