import './Auth.scss';
import { useState } from 'react';


function AuthForm() {

    const [isLogin, setIsLogin] = useState(true);

    return (
        <div className="auth col-3 p-4">

            <button 
            onClick={() => {setIsLogin(true)}}
            ></button>

            <form action="user/login" id="login" className='d-flex flex-column gap-3'>
                <input type="email" className="form-control" placeholder="Email" />
                <input type="password" className="form-control" placeholder="Password" />
                <label htmlFor="remember d-flex align-items-center gap-3">
                    <input type="checkbox" id="remember" name="remember" value="remember" />
                    <span>Remember me</span>
                </label>
                <button type="submit" className='btn btn-success'>Login</button>
            </form>
            <form action="user/registration" id="login" className='d-flex flex-column gap-3'>
                <input type="email" className="form-control" placeholder="Email" />
                <input type="password" className="form-control" placeholder="Password" />
                <label htmlFor="remember d-flex align-items-center gap-3">
                    <input type="checkbox" id="remember" name="remember" value="remember" />
                    <span>Remember me</span>
                </label>
                <button type="submit" className='btn btn-success'>Login</button>
            </form>
        </div>
    );
}

export default AuthForm;
