import './Auth.scss';
import { FormLogin, FormRegistration, FormRestore } from './components/Forms';
import { useState } from 'react';

function AuthPanel() {

    const [activeForm, setActiveForm] = useState('login');

    return (
        <div className="auth col-3 p-0">

            <div className="auth-tabs-controllers">
                <button 
                className={activeForm === 'login' ? 'active' : ''} 
                onClick={() => setActiveForm('login')}>
                    Login
                </button>
                <button 
                className={activeForm === 'registration' ? 'active' : ''} 
                onClick={() => setActiveForm('registration')}>
                    Registration
                </button>
                <button 
                className={activeForm === 'restore' ? 'active' : ''} 
                onClick={() => setActiveForm('restore')}>
                    Restore
                </button>
            </div>

            <div className="auth-tabs">
                <div className={`auth-tab ${activeForm === 'login' ? 'active' : '' }`}>
                    <FormLogin onSubmit={(data) => {
                        console.log(data);
                    }} />
                </div>
                <div className={activeForm === 'registration' ? 'active' : '' }>
                    <FormLogin onSubmit={(data) => {
                        console.log(data);
                    }} />
                </div>
                <div className={activeForm === 'restore' ? 'active' : '' }>
                    <FormLogin onSubmit={(data) => {
                        console.log(data);
                    }} />
                </div>
            </div>
        </div>
    );
}

export default AuthPanel;
