import './Auth.scss';
import { useState } from 'react';

function AuthForm() {

    const [isLogin, setIsLogin] = useState(true)
    const [isRegistration, setIsRegistration] = useState(false)
    const [isRestore, setIsRestore] = useState(false)

    function setAuth(type: 'login' | 'registration' | 'restore') {
        switch (type) {
            case 'login':
                setIsLogin(true)
                setIsRegistration(false)
                setIsRestore(false)
                break
            case 'registration':
                setIsLogin(false)
                setIsRegistration(true)
                setIsRestore(false)
                break
            case 'restore':
                setIsLogin(false)
                setIsRegistration(false)
                setIsRestore(true)
                break
            default:
                break
        }
    }

    return (
        <div className="auth col-3 p-0">
            <div className="auth-switchers py-3">
                <button 
                type='button'
                className={`link ${isLogin ? 'active' : ''}`}
                onClick={() => {
                    setAuth('login')
                }}
                >Login</button>
                <button 
                type='button'
                className={`link ${isRegistration ? 'active' : ''}`}
                onClick={() => {
                    setAuth('registration')
                }}
                >Registration</button>
            </div>


            <form 
            action="user/registration" 
            id="registration" 
            className={`auth-form ${isRegistration ? 'show' : 'hide'}`}
            onSubmit={(e) => {
                e.preventDefault()
                const formData = new FormData(e.target as HTMLFormElement);
                const data = Object.fromEntries(formData.entries());
                console.log(data);       
            }}
            >
                <h5 className="h5">Registration</h5>
                <input type="email" name="email" className="form-control" placeholder="Email" required/>
                <input type="password" name="password_2" className="form-control" placeholder="Password"  required/>
                <input type="password" name="password_2" className="form-control" placeholder="Repeat password"  required/>
                <button type="submit" className='btn btn-primary'>Registration</button>
            </form>

            <form 
            action="user/restore" 
            id="restore" 
            className={`auth-form ${isRestore ? 'show' : 'hide'}`}
            onSubmit={(e) => {
                e.preventDefault()
                const formData = new FormData(e.target as HTMLFormElement);
                const data = Object.fromEntries(formData.entries());
                console.log(data);       
            }}
            >
                <h5 className="h5">Restore Access</h5>
                <input type="email" name="email" className="form-control" placeholder="Email" />
                <button type="submit" className='btn btn-primary'>Find User</button>
            </form>
        </div>
    );
}

export default AuthForm;
