import './Auth.scss';
import { useEffect, useState } from 'react';

function AuthForm() {

    const [isLogin, setIsLogin] = useState(true)

    return (
        <div className="auth col-3 p-4">
            <div className="">
                <button 
                    className='btn'
                    onClick={() => {
                        setIsLogin(true)
                    }}
                >Login</button>
                <button 
                    className='btn'
                    onClick={() => {
                        setIsLogin(false)
                    }}
                >Registration</button>
            </div>

            <form action="user/login" id="login" className={`d-flex flex-column gap-3 auth-form ${isLogin ? 'show' : 'hide'}`}>
                <input type="email" name="email" className="form-control" placeholder="Email" />
                <input type="password" name="password" className="form-control" placeholder="Password" />
                <label htmlFor="remember d-flex align-items-center gap-3">
                    <input type="checkbox" id="remember" name="remember" value="remember" />
                    <span>Remember me</span>
                </label>
                <button type="submit" className='btn btn-success'>Login</button>
            </form>
            <form action="user/registration" id="login" className={`d-flex flex-column gap-3 auth-form ${isLogin ? 'hide' : 'show'}`}>
                <input type="email" name="email" className="form-control" placeholder="Email" />
                <input type="password" name="password_2" className="form-control" placeholder="Password" />
                <input type="password" name="password_2" className="form-control" placeholder="Repeat password" />
                <button type="submit" name="" className='btn btn-success'>Registration</button>
            </form>
        </div>
    );
}

export default AuthForm;
