import './Auth.scss';
import { useEffect, useState } from 'react';


function LoginForm() {

}

function AuthForm() {

    const [isLogin, setIsLogin] = useState(true)
    const [isRegistration, setIsRegistration] = useState(false)
    const [isRestore, setIsRestore] = useState(false)

    function setAuth(type: 'login' | 'registration' | 'restore') {
        switch (type) {
            case 'login':
                setIsLogin(true)
                setIsRegistration(false)
                setIsRestore(false)
                break
            case 'registration':
                setIsLogin(false)
                setIsRegistration(true)
                setIsRestore(false)
                break
            case 'restore':
                setIsLogin(false)
                setIsRegistration(false)
                setIsRestore(true)
                break
            default:
                break
        }
    }

    return (
        <div className="auth col-3 p-0">
            <div className="auth-switchers py-3">
                <button 
                    className={`link ${isLogin ? 'active' : ''}`}
                    onClick={() => {
                        setAuth('login')
                    }}
                >Login</button>
                <button 
                    className={`link ${isRegistration ? 'active' : ''}`}
                    onClick={() => {
                        setAuth('registration')
                    }}
                >Registration</button>
            </div>

            <form action="user/login" id="login" className={`auth-form ${isLogin ? 'show' : 'hide'}`}>
                <h5 className="h5">Restore Access</h5>
                <input type="email" name="email" className="form-control" placeholder="Email" />
                <input type="password" name="password" className="form-control" placeholder="Password" />
                <label htmlFor="remember" className="d-flex align-items-center gap-3 cursor-pointer unselect">
                    <input type="checkbox" id="remember" name="remember" value="remember" />
                    <span>Remember me</span>
                </label>
                <button type="submit" className='btn btn-primary'>Login</button>

                <button 
                    className={`link ${isRegistration ? 'active' : ''}`}
                    onClick={() => {
                        setAuth('restore')
                    }}
                >Restore Access</button>
            </form>
            <form action="user/registration" id="login" className={`auth-form ${isLogin ? 'hide' : 'show'}`}>
                <input type="email" name="email" className="form-control" placeholder="Email" />
                <input type="password" name="password_2" className="form-control" placeholder="Password" />
                <input type="password" name="password_2" className="form-control" placeholder="Repeat password" />
                <button type="submit" name="" className='btn btn-primary'>Registration</button>
            </form>
        </div>
    );
}

export default AuthForm;
