import './Auth.scss';
import { useState } from 'react';
import {  FormLogin, FormRegistration, FormRestore } from './components/Forms';
import { Tabs, TabsControl, Tab } from '../tabs/Tabs';

function AuthForm() {
    return (
        <div className="auth col-3 p-0">
            <div className="auth-switchers py-3">
                <Tabs>
                    <TabsControl>
                        <button onClick={() => setActiveTab('1')} className="btn btn-primary">Login</button>
                        <button onClick={() => setActiveTab('2')} className="btn btn-primary">Registration</button>
                        <button onClick={() => setActiveTab('3')} className="btn btn-primary">Restore</button>
                    </TabsControl>
                    <Tab id="1" title="Login">
                        <FormLogin onSubmit={(data) => console.log(data)} />
                    </Tab>
                    <Tab id="2" title="Registration">
                        <FormRegistration onSubmit={(data) => console.log(data)} />
                    </Tab>
                    <Tab id="3" title="Restore">
                        <FormRestore onSubmit={(data) => console.log(data)} />
                    </Tab>
                </Tabs>
            </div>
        </div>
    );
}

export default AuthForm;
